jQuery(document).ready(function(e){e("li.page_item_has_children").addClass("dropdown");e("li.page_item_has_children > a").addClass("dropdown-toggle");e("li.page_item_has_children > a").attr("data-toggle","dropdown");e("a.dropdown-toggle").append('<b class="caret"></b>');e("ul.children").addClass("dropdown-menu");e("ul.page-numbers").addClass("pagination");e("ul.page-numbers").addClass("pagination");e("[placeholder]").focus(function(){var t=e(this);if(t.val()==t.attr("placeholder")){t.val("");t.removeClass("placeholder")}}).blur(function(){var t=e(this);if(t.val()==""||t.val()==t.attr("placeholder")){t.addClass("placeholder");t.val(t.attr("placeholder"))}}).blur().parents("form").submit(function(){e(this).find("[placeholder]").each(function(){var t=e(this);t.val()==t.attr("placeholder")&&t.val("")})});e("#jumbotron-standard .item:first-child, #jumbotron-cinematic .item:first-child").addClass("active");e("#jumbotron-standard li, #jumbotron-cinematic li").addClass("active");e("button.woods-active").click(function(){e("li.metals, li.gemstones, #woods, #gemstones").removeClass("active");e("li.woods, #woods").addClass("active")});e("button.metals-active").click(function(){e("li.woods, li.gemstones, #woods, #gemstones").removeClass("active");e("li.metals, #metals").addClass("active")});e("button.gemstones-active").click(function(){e("li.woods, li.metals, #woods, #metals").removeClass("active");e("li.gemstones, #gemstones").addClass("active")})});